import React, { useEffect, useState } from "react";
import "../styles/CodeRain.css";

const CodeRain = () => {
  const [drops, setDrops] = useState([]);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    // Create rain drops
    const createRainDrop = () => {
      setDrops((prevDrops) => [
        ...prevDrops,
        {
          id: Math.random(),
          char: getRandomChar(),
          left: `${Math.random() * 100}vw`,
          duration: `${Math.random() * 3 + 6}s`,
          fontSize: `${Math.random() * 10 + 10}px`,
        },
      ]);

      if (drops.length > 100) {
        setDrops((prevDrops) => prevDrops.slice(1));
      }
    };

    const interval = setInterval(createRainDrop, 100);

    return () => clearInterval(interval);
  }, [drops]);

  // Track mouse movement
  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({ x: event.clientX, y: event.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const getRandomChar = () => {
    const chars = "0123456789ABCDEF";
    return chars[Math.floor(Math.random() * chars.length)];
  };

  return (
    <div
      className="code-rain"
      style={{
        background: `radial-gradient(
          circle at ${mousePos.x}px ${mousePos.y}px,
          rgba(95, 95, 95, 0.32) 5%,
          rgba(0, 0, 0, 0.95) 15%,
          rgba(0, 0, 0, 1) 50%

        )`,
      }}
    >
      {drops.map((drop) => (
        <div
          key={drop.id}
          className="code"
          style={{
            left: drop.left,
            animationDuration: drop.duration,
            fontSize: drop.fontSize,
            color: `rgba(4, 193, 200, ${
              Math.abs(mousePos.x - parseInt(drop.left)) < 100 ? 1 : 0.2
            })`,
          }}
        >
          {drop.char}
        </div>
      ))}
    </div>
  );
};

export default CodeRain;
