import React, { useEffect, useState } from "react";
import "../styles/CodeRain.css";

const CodeRain = () => {
  const [drops, setDrops] = useState([]);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    // Create rain drops
    const createRainDrop = () => {
      setDrops((prevDrops) => [
        ...prevDrops,
        {
            id: Math.random(),
            char: getRandomChar(),
            left: Math.random() * window.innerWidth, // Store as number for distance calculation
            top: -50, // Start above the viewport
            duration: Math.random() * 3 + 6, // Fall duration
            fontSize: Math.random() * 10 + 10,
        },
      ]);

      if (drops.length > 200) {
        setDrops((prevDrops) => prevDrops.slice(1));
      }
    };

    const interval = setInterval(createRainDrop, 100);

    return () => clearInterval(interval);
  }, [drops]);

  // Track mouse movement
  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({ x: event.clientX, y: event.clientY });
    };
    window.addEventListener("mousemove", handleMouseMove);

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const getRandomChar = () => {
    const chars = "0123456789ABCDEF";
    return chars[Math.floor(Math.random() * chars.length)];
  };

  const moveDrops = () => {
    setDrops((prevDrops) =>
      prevDrops.map((drop) => ({
        ...drop,
        top: drop.top + 5, // Move down by 5px
      }))
    );
  };

  useEffect(() => {
    const interval = setInterval(moveDrops, 50);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="code-rain">
      {drops.map((drop) => {
        // Calculate the distance to the mouse
        const distance = Math.sqrt(
            Math.pow(mousePos.x - drop.left, 2) +
              Math.pow(mousePos.y - drop.top, 2)
        );

        // Adjust brightness based on distance
        const brightness = Math.max(1 - distance / 200, 0); // 200px radius for torchlight effect

        return (
            <div
                key={drop.id}
                className="code"
                style={{
                    position: "absolute",
                    left: `${drop.left}px`,
                    top: `${drop.top}px`,
                    fontSize: `${drop.fontSize}px`,
                    color: `rgba(0, 255, 0, ${brightness})`,
                }}
            >
            {drop.char}
            </div>
      )})}
    </div>
  );
};

export default CodeRain;
