import React from 'react';
import '../styles/AboutMe.css';

const skills = [
  { name: 'HTML', level: 90 },
  { name: 'CSS', level: 80 },
  { name: 'JavaScript', level: 85 },
  { name: 'React', level: 75 },
  { name: 'Django', level: 70 },
];

const AboutMe = () => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
        const aboutMeSection = document.getElementById('about');
        const rect = aboutMeSection.getBoundingClientRect();
        if (rect.top < window.innerHeight && rect.bottom > 0) {
            setVisible(true);
        }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);


  return (
    <section id="about-me" className="about-me">
      <div className="about-picture">
        <img src="/images/me.png" alt="Your Name" />
        <h2>About Me</h2>
        <p>I was a full-stack developer / software tester for the French Army in Paris, France.
After a long break sailing through Europe, I'm coming back to my roots and programming.
I like responsive and dynamic user interfaces, and intuitive applications.
Contact me to discuss work opportunity.</p>
      </div>
      <div className="about-description">
        <ul className="skills">
          <li>HTML</li>
          <li>CSS</li>
          <li>JavaScript</li>
          <li>React</li>
          <li>Node.js</li>
        </ul>
      </div>
    </section>
  );
};

export default AboutMe;
