import React, { useState, useRef, useEffect } from 'react';
import '../styles/MyWork.css';

const projects = [
  { id: 1, name: 'La Ferme', skills: ['HTML', 'CSS', 'React'], url: '/images/projects/laferme.jpg' },
  { id: 2, name: 'Price Comparator', skills: ['JavaScript', 'Python', 'Django'], url: '/images/projects/comparator.jpg' },
  { id: 3, name: 'Workshop', skills: ['JavaScript', 'Python', 'Django'], url: '/images/projects/workshop.jpg' },
  { id: 4, name: 'S.I.R', skills: ['HTML', 'CSS', 'PHP'], url: '/images/projects/sir.jpg' },
];

const MyWork = () => {
  const [activeFilter, setActiveFilter] = useState('All');
  const [rotate, setRotate] = useState(false);
  const filters = ['All', 'React', 'JavaScript', 'HTML'];
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (!entry.target) return;

          const sectionTitle = entry.target.querySelector(".my-work-title");
          const sectionSeparator = entry.target.querySelector(".separator");
          const sectionAboutDescr = entry.target.querySelector(".filters");
          const progressBarFills = entry.target.querySelectorAll(".project");
          if (entry.isIntersecting) {
            sectionTitle?.classList.add("animate");
            sectionSeparator?.classList.add("animate");
            sectionAboutDescr?.classList.add("animate");
            progressBarFills?.forEach((fill) => {
              fill.classList.add("animate");
            });
          } else {
            sectionTitle?.classList.remove("animate");
            sectionSeparator?.classList.remove("animate");
            sectionAboutDescr?.classList.remove("animate");
            progressBarFills?.forEach((fill) => {
              fill.classList.remove("animate");
            });
          }
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    // Refresh animations when the activeFilter changes
    const projects = document.querySelectorAll('.project');
    projects.forEach((project) => {
      project.classList.add('animate');
    });
  }, [activeFilter]);


  const handleButtonClick = (filter) => {
    if (activeFilter === filter) return;
    setRotate(true);
    setActiveFilter(filter);
    setTimeout(() => setRotate(false), 500);
  };

  const filteredProjects =
    activeFilter === 'All' ? projects : projects.filter((p) => p.skills.includes(activeFilter));

  return (
    <section id="my-work" className="my-work" ref={sectionRef}>
      <h1 className="my-work-title">&lt; MY WORK &gt;</h1>
      <div className='separator'></div>
      <div className='my-work-content'>
        <div className="filters">
          {filters.map((filter) => (
          <button
            key={filter}
            className={`filter-button ${activeFilter === filter ? 'active' : ''} ${
              rotate && activeFilter === filter ? 'rotate' : ''
            }`}
            onClick={() => handleButtonClick(filter)}
          >
            <div className="text-layer default">{filter}</div>
            <div className="text-layer hover">{filter}</div>
          </button>
          ))}
        </div>
        <div className="projects">
          {filteredProjects.map((project) => (
            <div
              key={project.id}
              className="project"
              style={{
                backgroundImage: `url(${project.url})`,
              }}
            >
              <div className="project-description">
                <h3>{project.name}</h3>
                <p>Skills: {project.skills.join(', ')}</p>
              </div>
              <button className="project-button">Learn More</button>
            </div>
          ))}
          {filteredProjects.length === 0 && (
            <p className="no-projects">No projects match this filter.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default MyWork;
