import React, { useRef, useEffect} from 'react';
import '../styles/Home.css';

const Home = () => {
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (!entry.target) return;

          const sectionMenu = entry.target.querySelector(".dropdown-menu");
          if (entry.isIntersecting) {
            sectionMenu?.classList.add("hide");
          } else {
            sectionMenu?.classList.remove("hide");
          }
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  return (
    <section id="home" className="home" ref={sectionRef}>
      <div className='home-content'>
        <h1 className="home-title">RÃ©my Santoriello</h1>
        <p className="home-tagline">Full-Stack Developer | Turning Ideas into Reality</p>
        <div className="home-links">
          <a href="#about-me" className="home-btn">More About Me</a>
          <a href="#my-work" className="home-btn">My Work</a>
        </div>
      </div>
    </section>
  );
};

export default Home;
