import React, { useState } from 'react';
import '../styles/MyWork.css';

const projects = [
  { id: 1, name: 'La Ferme', skills: ['HTML', 'CSS', 'React'], url: '/images/projects/laferme.jpg' },
  { id: 2, name: 'Price Comparator', skills: ['JavaScript', 'Python', 'Django'], url: '/images/projects/comparator.jpg' },
  { id: 3, name: 'Workshop', skills: ['JavaScript', 'Python', 'Django'], url: '/images/projects/workshop.jpg' },
  { id: 4, name: 'S.I.R', skills: ['HTML', 'CSS', 'PHP'], url: '/images/projects/workshop.jpg' },
];

const MyWork = () => {
  const [filter, setFilter] = useState('All');
  const [hoveredProject, setHoveredProject] = useState(null);


  const filteredProjects =
    filter === 'All' ? projects : projects.filter((p) => p.skills.includes(filter));

  return (
    <section id="my-work" className="my-work">
      <h1>&lt; MY WORK &gt;</h1>
      <div className='separator'></div>
      <div className='my-work-content'>
        <div className="filters">
          <button onClick={() => setFilter('All')}>All</button>
          <button onClick={() => setFilter('React')}>React</button>
          <button onClick={() => setFilter('JavaScript')}>JavaScript</button>
          <button onClick={() => setFilter('HTML')}>HTML</button>
        </div>
        <div className="projects">
          {filteredProjects.map((project) => (
            <div
              key={project.id}
              className="project"
              style={{
                backgroundImage:
                hoveredProject === project.id ? 'none' : `url(${project.url})`,
              }}
              onMouseEnter={() => setHoveredProject(project.id)}
              onMouseLeave={() => setHoveredProject(null)}
            >
              <div className="project-description">
                <h3>{project.name}</h3>
                <p>Skills: {project.skills.join(', ')}</p>
              </div>
              <button className="project-button">Learn More</button>
            </div>
          ))}
          {filteredProjects.length === 0 && (
            <p className="no-projects">No projects match this filter.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default MyWork;
