import React, { useState } from 'react';
import '../styles/MyWork.css';

const projects = [
  { id: 1, name: 'Project 1', skills: ['React', 'Node.js'] },
  { id: 2, name: 'Project 2', skills: ['HTML', 'CSS'] },
  { id: 3, name: 'Project 3', skills: ['JavaScript'] },
];

const MyWork = () => {
  const [filter, setFilter] = useState('All');

  const filteredProjects =
    filter === 'All' ? projects : projects.filter((p) => p.skills.includes(filter));

  return (
    <section id="my-work" className="my-work">
      <h1>&lt; MY WORK &gt;</h1>
      <div className='separator'></div>
      <div className='my-work-content'>
        <div className="filters">
          <button onClick={() => setFilter('All')}>All</button>
          <button onClick={() => setFilter('React')}>React</button>
          <button onClick={() => setFilter('JavaScript')}>JavaScript</button>
          <button onClick={() => setFilter('HTML')}>HTML</button>
        </div>
        <div className="projects">
          {filteredProjects.map((project) => (
            <div key={project.id} className="project">
              <div className="project-description">
                <h3>{project.name}</h3>
                <p>Skills: {project.skills.join(', ')}</p>
              </div>
              <button>Learn More</button>
            </div>
          ))}
          {filteredProjects.length === 0 && (
            <p className="no-projects">No projects match this filter.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default MyWork;
